composers=[]


def make_notes():
    big_dict_energy = {}
    
    filepath=os.path.join(os.getcwd(), "new")
    for folders in (os.listdir(filepath)):
        if(folders!=".DS_Store"):
            notes=[]
            to_iterate=os.path.join(filepath,folders)
            composers.append(folders)
            for filename in os.listdir(to_iterate):
                if(filename!=".DS_Store"):
                    if("format0" not in filename):
                        file=os.path.join(to_iterate,filename)
                        
                        midi = converter.parse(file)
                        print("Parsing %s" % file)
                
                        notes_to_parse = None
                        
                        partStream = midi.parts.stream()
#                        
#                        instrument_list = []
#                        
                        for instrument in partStream:
#                            
#                    
#                        
#                        instrument_1 = partStream[0]
#                        
#                        instrument_1.flat.notes
#                        
#                        for note in instrument.flat.notes:
#                            print(note.duration)
                        
                        
                        
                            
                            for element in instrument.flat.elements:
                                if instrument.partName == None:
                                    instrument_string = 'Piano'
                                else:
                                    instrument_string = instrument.partName
                                    instrument_string=instrument_string.split()
                                    instrument_string = ''.join(instrument_string)
                                if isinstance(element,note.Rest):
                                    #print("yes",element.duration.quarterLength,notes)
                                    toAdd=str(instrument_string)+":"+"Z"+":"+str(element.duration.quarterLength)
                                    notes.append(toAdd)
                                
                                
                                elif isinstance(element,note.Note):
                                    toAdd=str(instrument_string)+":"+str(element.pitch)+":"+str(element.duration.quarterLength)+":"+str(element.volume.velocity)
                                    #print(element.pitch)
                                    notes.append(toAdd)
    
                                    
                                 
                                    
                                elif isinstance(element, chord.Chord):
                                    toAdd='*'.join(str(n) for n in element.normalOrder)
                                    toAdd=str(instrument_string)+":"+toAdd+":"+str(element.duration.quarterLength)+":"+str(element.volume.velocity)
                                    notes.append(toAdd)
            print(notes)

            big_dict_energy[folders]=notes
            
            
           
            
            
            
        #print(notes)
    return big_dict_energy



returned_value=make_notes()



for names in composers:
    #output some text file
    file_path=os.path.join(os.getcwd(),"output")
    output_path=os.path.join(file_path,names)
    output_file=open(output_path+".txt", "w")
    to_output=' '.join(returned_value[names])
    output_file.write(to_output)
    output_file.close()
    

